package com.company;import java.util.Scanner;public class VowelsCount_02 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String text = scanner.nextLine();        countVowels(text);    }    public static void countVowels(String input) {        input = input.toLowerCase();        int counter = 0;        for (int i = 0; i < input.length(); i++) {            if (input.charAt(i) == 'a' || input.charAt(i) == 'e' || input.charAt(i) == 'o'                    || input.charAt(i) == 'i' || input.charAt(i) == 'u') {                counter++;            }        }        System.out.println(counter);    }}