package com.company;import java.util.Scanner;public class DigitsLettersAndOther_05 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        System.out.println(onlyDigits(input));        System.out.println(onlyLetters(input));        System.out.println(othersSymbols(input));    }    private static String onlyDigits(String input) {        StringBuilder stringBuilder = new StringBuilder();        for (int i = 0; i < input.length() ; i++) {            char currentChar = input.charAt(i);            if (Character.isDigit(currentChar)){                stringBuilder.append(currentChar);            }        }        return stringBuilder.toString();    }    private static String onlyLetters(String input) {        StringBuilder stringBuilder = new StringBuilder();        for (int i = 0; i < input.length() ; i++) {            char currentChar = input.charAt(i);            if (Character.isLetter(currentChar)){                stringBuilder.append(currentChar);            }        }        return stringBuilder.toString();    }    private static String othersSymbols(String input) {        StringBuilder stringBuilder = new StringBuilder();        for (int i = 0; i < input.length() ; i++) {            char currentChar = input.charAt(i);            if (!Character.isLetterOrDigit(currentChar)){                stringBuilder.append(currentChar);            }        }        return stringBuilder.toString();    }}