package com.company;import java.util.Scanner;public class RepeatString_02 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String[] input = scanner.nextLine().split(" ");        for (String currentText : input) {            System.out.print(appendString(currentText));        }    }     static String appendString(String currentText) {        StringBuilder stringBuilder = new StringBuilder();         for (int i = 0; i <currentText.length() ; i++) {             stringBuilder.append(currentText);         }         return stringBuilder.toString();    }}