package com.company;import java.util.Scanner;public class ReveseString_01 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String input = scanner.nextLine();        while (!input.equals("end")) {            System.out.printf("%s = %s%n", input,printRreverseString(input));            input = scanner.nextLine();        }    }     static String printRreverseString(String input) {        StringBuilder stringBuilder = new StringBuilder();        for (int i = input.length() - 1; i >= 0; i--) {            stringBuilder.append(input.charAt(i));        }         return stringBuilder.toString();    }}