package com.company;import java.util.Scanner;public class TextFilter_04 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String [] bannedWords = scanner.nextLine().split(", ");        String text = scanner.nextLine();        for (int i = 0; i < bannedWords.length ; i++) {            String currentBannedWord = bannedWords[i];            while (text.contains(currentBannedWord)){                text = text.replace(currentBannedWord,lenghtAsterisks(currentBannedWord) );            }        }        System.out.println(text);    }    private static String lenghtAsterisks(String currentBannedWord) {        StringBuilder stringBuilder = new StringBuilder();        for (int i = 0; i < currentBannedWord.length() ; i++) {            stringBuilder.append("*");        }        return stringBuilder.toString();    }}